sys_ui_page.client_script:
/*
What is the difference between g:evaluate and g2:evaluate?

Order of execution is g:evaluate then g2:evaluate

g:evaluate results are cached and do not change for life of the page.
	Use ${} to evaluate the expressions in HTML

g2:evaluate results are determined during each page refresh
	Use $[] to evaluate expresssions in the HTML
*/

var myScriptInclude = Class.create();

	myScriptInclude.prototype = {
		initialize: function() {},

		getAnything: function() {
			return "anything";
		},

		yearChoices: function(){
			var currentYear = new Date().getFullYear();
			var startYear = currentYear - 2;
			var endYear = currentYear + 2;
			var yearRange = [];

			for (var year = startYear; year <= endYear; year++) {
				yearRange.push(year);
			}

			// Set the calculated year range as a global variable to be used in the UI Page
			return yearRange;
		}
	};

