function chgYear () {
        desiredRRYear = document.getElementById('yearSelect').value;
		document.getElementById('rrTitle').innerHTML = 'Receiving Report Month (' + desiredRRMonth + ') and Year (' + desiredRRYear + ') Choices';
    }

function chgMonth() {
        desiredRRMonth = document.getElementById('monthSelect').value;
		document.getElementById('rrTitle').innerHTML = 'Receiving Report Month (' + desiredRRMonth + ') and Year (' + desiredRRYear + ') Choices';
    }

function getMonthName(monthNumber) {
    var months = [
        'January', 'February', 'March', 'April',
        'May', 'June', 'July', 'August',
        'September', 'October', 'November', 'December'
    ];
    return months[monthNumber - 1];
}
