				html += '<tr style="height: 23pt;"><td style="width: 22pt; border-top-style: solid; border-top-width: 1pt; border-bottom-style: solid; border-bottom-width: 1pt; border-right-style: solid; border-right-width: 1pt;text-align: center;"><div id="' + formattedRow +'Cell01">' + '&nbsp;' + '<div></td><td style="width: 294pt; border-top-style: solid; border-top-width: 1pt; border-left-style: solid; border-left-width: 1pt; border-bottom-style: solid; border-bottom-width: 1pt; border-right-style: solid; border-right-width: 2pt;" colspan="5"><div id="' + formattedRow +'Cell02">' + '&nbsp;' + '&nbsp;' + '&nbsp;' + '<br/>' + '&nbsp;' + '<div></td>' + '</td><td style="width: 38pt; border-top-style: solid; border-top-width: 1pt; border-left-style: solid; border-left-width: 2pt; border-bottom-style: solid; border-bottom-width: 1pt; border-right-style: solid; border-right-width: 1pt;background-color:#D3D3D3;" colspan="2"><div id="' + formattedRow +'Cell03">' +  '&nbsp;' + '<div></td>' + '</td><td style="width: 23pt; border-top-style: solid; border-top-width: 1pt; border-left-style: solid; border-left-width: 1pt; border-bottom-style: solid; border-bottom-width: 1pt; border-right-style: solid; border-right-width: 1pt;background-color:#D3D3D3;"><div id="' + formattedRow +'Cell04">' +  '&nbsp;' + '<div></td>' + '</td><td style="width: 40pt; border-top-style: solid; border-top-width: 1pt; border-left-style: solid; border-left-width: 1pt; border-bottom-style: solid; border-bottom-width: 1pt; border-right-style: solid; border-right-width: 2pt;background-color:#D3D3D3;" colspan="2"><div id="' + formattedRow +'Cell05">' +  '&nbsp;' + '<div></td>' + '<td style="width: 41pt; border-top-style: solid; border-top-width: 1pt; border-left-style: solid; border-left-width: 2pt; border-bottom-style: solid; border-bottom-width: 1pt; border-right-style: solid; border-right-width: 1pt;text-align: center;"><div id="' + formattedRow +'Cell06">' + '&nbsp;' + '<div></td>'+ '</td><td style="width: 22pt; border-top-style: solid; border-top-width: 1pt; border-left-style: solid; border-left-width: 1pt; border-bottom-style: solid; border-bottom-width: 1pt; border-right-style: solid; border-right-width: 1pt;"><div id="' + formattedRow +'Cell07">' +  '&nbsp;' + '<div></td>' + '<td style="width: 42pt; border-top-style: solid; border-top-width: 1pt; border-left-style: solid; border-left-width: 1pt; border-bottom-style: solid; border-bottom-width: 1pt; border-right-style: solid; border-right-width: 1pt;text-align: right;"><div id="' + formattedRow +'Cell08">' + '&nbsp;' + '<div></td>'+ '</td><td style="width: 52pt; border-top-style: solid; border-top-width: 1pt; border-left-style: solid; border-left-width: 1pt; border-bottom-style: solid; border-bottom-width: 1pt;ext-align: right;"><div id="' + formattedRow +'Cell09">' +  '&nbsp;' + '<div></td>' + '</tr>';
				}
			}
			// Close the HTML table
			html += '</td></tr></tbody></table></div>';
			step = 5;
			// alert(step + '. ');
		} else {
			step = 6;
			alert(step + '. answer catch: ' + error.toString());
		}		
		// document.getElementById('render_table').innerHTML = html;
		renderTheTable(html, 'getRecordsJSON');
		var currNameOfSeller = getDesiredRRVendor();
		if (currNameOfSeller !== null) {
			if(typeof currNameOfSeller === 'string' && currNameOfSeller.trim() !== ''){
				document.getElementById('nameOfSeller').innerHTML = '<div>' + currNameOfSeller + '</div>';	
			}
		}

		step = 7;
	}
	catch (error){
		alert('from client catch: ' + error.toString());
		// html = step + '. from client catch: ' + error.toString() + '<br />' + answer;
	}

}

function renderYearDropDown(defYear) {
	try {
		var anotherOption = '';
		step = 8;
		var currentYear = new Date().getFullYear();
		//alert(step + '. currentYear is ' + currentYear);
		step = 9;
		var yearChoices = [currentYear - 2, currentYear - 1, currentYear, currentYear + 1, currentYear + 2];
		step = 10;
// 		var defaultYear =  parseInt(getDesiredRRYear()); // new Date().getFullYear();
// 		step = 11;
		// Populate year options
		var htmlSelectYear = ''
		+ '<label for="yearSelect">Select Year:</label>'
		+ '<select id="yearSelect" onChange="chgYear()">'
		+ '<option value=""></option>';
		step = 12;
		var htmlSelectClose = ''
		+'</select>';
		step = 13;
		// alert(step + '. htmlSelectYear is ' + htmlSelectYear);	

		var intCurrentlySelectedYear = parseInt(defYear);
		for (var i = 0; i < yearChoices.length; i++) {
			anotherOption = '<option ';
			if (yearChoices[i] === intCurrentlySelectedYear) {
				anotherOption = anotherOption + ' selected '; // Set the default year as selected
			}
			anotherOption = anotherOption + 'value="' + yearChoices[i] + '">';

			anotherOption = anotherOption +  yearChoices[i] + '</option>';

			htmlSelectYear = htmlSelectYear + anotherOption;
		}
		step = 14;
		htmlSelectYear = htmlSelectYear + htmlSelectClose;

		document.getElementById('render_yearSelect').innerHTML = '<div>' + htmlSelectYear + '</div>';
	}
	catch (error){
		document.getElementById('render_yearSelect').innerHTML = '<div>' + error.toString() + '</div>';
	}
}

function getMonthName(monthNumber) { // param expected to be between 0 and 11
    var months = [
        'January', 'February', 'March', 'April',
        'May', 'June', 'July', 'August',
        'September', 'October', 'November', 'December'
    ];
	var intMonthNumber = parseInt(monthNumber);
	var zeroBasedMonthNumber = intMonthNumber-1;
	// alert ('getMonthName('+ zeroBasedMonthNumber.toString() +') is ' + months[intMonthNumber-1]);
    return months[intMonthNumber-1]; // javascript arrays are zero-based
}

function renderMonthDropDown(defMonth) {
		step = 15;

// 		var currentDate = new Date();
// 		setDesiredRRMonth(currentDate.setMonth(currentDate.getMonth() - 1));
		step = 16.04;
		// var previousMonth =  getDesiredRRMonth(); 
		// currentDate.getMonth() +1; 
		// Months are 0-based but my xref is 1-12
		// alert('previousMonth is ' + previousMonth);
		step = 16.05;
		document.getElementById('render_monthSelect').innerHTML = '';
		step = 16.06;
		var selHTML = '<label for="monthSelect">Select Month:</label>';
		selHTML += '<select id="monthSelect" onChange="chgMonth()">';
		selHTML += '<option value=""></option>';
		step = 16.07;
		for (var month = 1; month <= 12; month++) {
			selHTML += '<option value="' + month + '"';
			if (month === defMonth) {
				selHTML += ' selected';
			}
			selHTML += '>' + getMonthName(month) + '</option>'; // month-1 is between 0 and 11
		}
		step = 16.08;
		selHTML += '</select>';
		// alert(step + '. selHTML is ' + selHTML);
		document.getElementById('render_monthSelect').innerHTML = '<div>' + selHTML + '</div>';
		step = 16.09;
}

